function initThumbnail(){
  var thumbnail = $('.thumbnail')
  var action = thumbnail.find('.action a')

  action.unbind().on('click', function(event){
    event.preventDefault()
    if($(this).hasClass('active')){
      $(this).removeClass('active')
    } else {
      $(this).addClass('active')
    }
  })
}

function initMenu(){
  var menu = $("#navbar .navbar-menu a")
  var contentSection = $("#dashboard-content section")

  menu.unbind().on('click', function(event){
    event.preventDefault()

    menu.removeClass('active')
    $(this).addClass('active')

    contentSection.removeClass('active')
    $($(this).attr('href')).addClass('active')
  })
}

function initDashboard(){
  initMenu()
  initThumbnail()
}

$(function(){
  //verify(initDashboard(), false, 'p.signin.html')
  initPage()
  initDashboard()
})
